show databases;
CREATE DATABASE  IF NOT EXISTS `project2nd4`;
USE `project2nd4`;
SET NAMES utf8 ;
DROP TABLE IF EXISTS `boardtype`;
SET character_set_client = utf8mb4 ;

CREATE TABLE `boardtype` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `board_name` varchar(45) NOT NULL COMMENT '게시판이름',
  PRIMARY KEY (`id`)
);

CREATE TABLE `department` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `dept_name` varchar(15) NOT NULL COMMENT '부서명',
  PRIMARY KEY (`id`)
);
CREATE TABLE `staff` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `staff_id` varchar(20) NOT NULL COMMENT '아이디',
  `password` varchar(45) NOT NULL COMMENT '비밀번호',
  `email` varchar(45) NOT NULL COMMENT '이메일',
  `phoneNumber` varchar(15) NOT NULL COMMENT '핸드폰 번호',
  `staff_name` varchar(10) NOT NULL COMMENT '이름',
  `birthday` datetime NOT NULL COMMENT '생년월일',
  `image` varchar(50) DEFAULT 'default.png',
  `isAdmin` varchar(1) DEFAULT 'N',
  `isManager` varchar(1) DEFAULT 'N',
  `dept_id` int(11) NOT NULL COMMENT '부서번호',
  `self_introduce` text,
  PRIMARY KEY (`id`),
  KEY `FK_Staff_dept_id_Department_id` (`dept_id`),
  CONSTRAINT `FK_Staff_dept_id_Department_id` FOREIGN KEY (`dept_id`) REFERENCES `department` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
);
CREATE TABLE `post` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '글번호',
  `title` varchar(45) NOT NULL COMMENT '글제목',
  `content` text NOT NULL COMMENT '내용',
  `writer_id` int(11) NOT NULL COMMENT '글쓴이',
  `time` datetime NOT NULL COMMENT '작성시간',
  `count` int(11) NOT NULL COMMENT '조회수',
  `boardtype_id` int(11) NOT NULL COMMENT '게시판종류',
  `file_name` varchar(100) DEFAULT '',
  PRIMARY KEY (`id`),
  KEY `FK_Post_writer_id_Staff_id` (`writer_id`),
  KEY `FK_Post_boardtype_id_BoardType_id` (`boardtype_id`),
  CONSTRAINT `FK_Post_boardtype_id_BoardType_id` FOREIGN KEY (`boardtype_id`) REFERENCES `boardtype` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FK_Post_writer_id_Staff_id` FOREIGN KEY (`writer_id`) REFERENCES `staff` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE `major` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `major_name` varchar(15) NOT NULL COMMENT '전공명',
  PRIMARY KEY (`id`)
);

CREATE TABLE `professor` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '교수 고유번호',
  `prof_name` varchar(10) NOT NULL COMMENT '교수 이름',
  `major_id` int(11) NOT NULL COMMENT '전공번호',
  PRIMARY KEY (`id`),
  KEY `FK_Professor_major_id_Major_id` (`major_id`),
  CONSTRAINT `FK_Professor_major_id_Major_id` FOREIGN KEY (`major_id`) REFERENCES `major` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE `lecturetype` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `lecture_type` varchar(45) NOT NULL COMMENT '종류',
  PRIMARY KEY (`id`)
);

CREATE TABLE `lecture` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `lecture_name` varchar(45) NOT NULL COMMENT '강의명',
  `credit` int(11) NOT NULL COMMENT '학점',
  `time` varchar(15) NOT NULL COMMENT '시간',
  `lecture_type_id` int(11) NOT NULL COMMENT '종별아이디',
  `prof_id` int(11) NOT NULL COMMENT '교수아이디',
  PRIMARY KEY (`id`),
  KEY `FK_Lecture_prof_id_Professor_id` (`prof_id`),
  KEY `FK_Lecture_class_id_Class_id` (`lecture_type_id`),
  CONSTRAINT `FK_Lecture_class_id_Class_id` FOREIGN KEY (`lecture_type_id`) REFERENCES `lecturetype` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FK_Lecture_prof_id_Professor_id` FOREIGN KEY (`prof_id`) REFERENCES `professor` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE `message` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `content` text NOT NULL COMMENT '내용',
  `time` datetime NOT NULL COMMENT '작성시간',
  `receiver_id` int(11) NOT NULL COMMENT '받는 사람',
  `sender_id` int(11) NOT NULL COMMENT '보낸 사람',
  PRIMARY KEY (`id`),
  KEY `FK_Message_receiver_id_Staff_id` (`receiver_id`),
  CONSTRAINT `FK_Message_receiver_id_Staff_id` FOREIGN KEY (`receiver_id`) REFERENCES `staff` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE `comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '댓글번호',
  `writer_id` int(11) NOT NULL COMMENT '글쓴이',
  `time` datetime NOT NULL COMMENT '작성시간',
  `content` varchar(50) NOT NULL COMMENT '내용',
  `refer` int(11) NOT NULL COMMENT '원본글',
  `recomment` varchar(1) NOT NULL DEFAULT 'N',
  `refer_comment` int(11) DEFAULT '1',
  `recomment_count` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_Comment_refer_Post_id` (`refer`),
  CONSTRAINT `FK_Comment_refer_Post_id` FOREIGN KEY (`refer`) REFERENCES `post` (`id`) ON DELETE CASCADE
);
